import React from 'react';
import ContactForm from '../components/ContactForm';

class ShowAnimal extends React.Component {
  state = {
    name: '',
    age: '',
    breed: '',
    color: '',
    size: '',
    gender: '',
    street_address: '',
    city: '',
    zip: '',
    state: '',
    email: '',
    phone: ''
  };

  componentDidMount() {
    console.log(this.props.match.params);
    fetch(`/api/animals/${this.props.match.params.id}`)
      .then(response => response.json())
      //   .then(data => console.log(data));
      .then(data =>
        this.setState({
          name: data.name,
          age: data.age,
          breed: data.breeds.primary,
          color: data.colors.primary,
          size: data.size,
          gender: data.gender,
          street_address: data.contact.address.address1,
          city: data.contact.address.city,
          state: data.contact.address.state,
          zip: data.contact.address.postcode,
          email: data.contact.email,
          phone: data.contact.phone,
          photo: data.image
        })
      );
  }

  render() {
    return (

        <>   
        <div>
          {this.state.name}
        {this.state.age}
        {this.state.breed}
        {this.state.color}
        {this.state.size}
        {this.state.gender}
        {this.state.street_address}
        {this.state.city}
        {this.state.state}
        {this.state.zip}
        {this.state.email}
        {this.state.phone}
        {this.state.photo}
        </div>



        <ContactForm />
      </>
    );
  }
}

export default ShowAnimal;

